import jwt from 'jsonwebtoken';
import { getGoogleUser } from '../utils/getGoogleUser';
import { updateOrCreateUserFromOauth } from '../utils/updateOrCreateUser';

export const googleOauthCallbackRoute = {
    path: '/auth/google/callback',
    method: 'get',
    handler: async (req, res) => {
        const { code } = req.query;        
        const oauthUserInfo = await getGoogleUser({ code });
        const updatedUser = await updateOrCreateUserFromOauth({ oauthUserInfo });
        const { _id: id, isVerified, email,name,picture,given_name,Upvoted } = updatedUser;

        jwt.sign(
            { id, isVerified, email,name,picture,given_name,Upvoted },
            process.env.JWT_SECRET,
            (err, token) => {
                if (err) return res.sendStatus(500);
                res.redirect(`http://localhost:3000/signin?token=${token}`)
            }
        );
    }
};

