import { getDbConnection } from '../db';


export const searchRoute = {
    path: '/api/blogs',
    method: 'get',
    handler: async (req, res) => {
        const blogName = req.body;
        console.log(blogName,"now");
        const db = getDbConnection('techarena');
        const articleInfo = await db.collection('blogs').findOne({ name: blogName });
        res.status(200).json(articleInfo);
        // await db.collection('blogs').find({ name: blogName }).toArray(function(err, result) {
        //     if (err) throw err;
        //     res.status(200).json(result);
        //   });
    },
};
